import * as actionTypes from './actionTypes';
import _ from 'lodash';
const initialState = {
    getDataState: 'INIT',
    data:[],
    error:'',
    lastNullQuery:''
}

const initState = () => {
    return initialState;
}

//GET DATA REDUCERS
const getDataLoading = (state) => {
    return {...state, 
        getDataState: 'LOADING'
    } ;
}

const getDataSuccess = (state, action) => {
    allData = [];
    console.log("ALLLADATA",allData);

    _.forEach(action.data.hits, eachData =>{
        allData = allData.filter(obj =>
            _.includes(obj.objectID, eachData) ?
              true : false
          )
    });


    allData = _.concat(allData,action.data.hits)
    console.log("objectID",allData);
    return {...state,
        getDataState: 'SUCCESS',
        data: allData,
        lastNullQuery:action.lastNullQuery,
        error: null
    }
};

const getDataError = (state, action) => {
    return {...state,
        getDataState: 'ERROR',
        error: action.error,
    };
};


const reducer = (state = initialState, action) => {

    switch (action.type) {

        case actionTypes.INIT_STATE: return initState();

        case actionTypes.GET_DATA_LOADING: return getDataLoading(state, action);
        case actionTypes.GET_DATA_SUCCESS: return getDataSuccess(state, action);
        case actionTypes.GET_DATA_ERROR: return getDataError(state, action);

        default: return state;
    }
};

export default reducer;
