import * as actionTypes from './actionTypes';
import axios from 'axios';
import _ from 'lodash';
export const initState = () => {
    return dispatch => {
        dispatch({
            type: actionTypes.INIT_STATE
        })
    };
};

let dataArray = [];
let count = 0;
let errMsg = '';
let lastNullQuery = '';
function recursiveGetData(query, page, threshold, callback) {

    let url = "http://hn.algolia.com/api/v1/search?query=" + query + "&page=" + page;
    return axios.get(url).then(function (response) {

        if (response.data.results.length > 0) {
            if (!_.isEmpty(dataArray)) {

                response.data.results.forEach(element => {
                    if (!(dataArray.some(function (o) { return (_.isEqual(o["url"], element.url)) }))) {
                        if (_.includes(element.name.toLowerCase(), query.toLowerCase())) {
                            count = count + 1;
                        }
                        dataArray.push(element);
                    }

                });
            }
            else {

                dataArray = _.cloneDeep(response.data.results);
                dataArray.forEach(element => {
                    if (_.includes(element.name.toLowerCase(), query.toLowerCase())) {
                        count = count + 1;
                    }
                });
            }
            if (count < threshold && !_.isEmpty(response.next)) {
                return recursiveGetData(query, page + 1, threshold);
            }
            else {
                if (response.data.next === null)
                {
                    lastNullQuery = query;
                }
                callback();
                return dataArray;

            }
        }
        else {
            if (response.data.next === null)
                {
                    lastNullQuery = query;
                }


            callback();
            return dataArray;

        }
    }
    ).catch(error => {
