import React, { Component } from 'react';
import { connect } from 'react-redux';
import searchIcon from './search.png';
import './index.css';
import * as actions from './Store/action';
import cx from 'classnames';
import { NavLink } from "react-router-dom";
import _ from 'lodash';
class HomePage extends Component {

  constructor(props) {
    super(props)
    this.state = {
      query: '',
      result: [],
      threshold: 3,
      focusedItem: -1
    }
  }

  componentDidMount() {
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.data !== this.props.data) {
      this.searchFilter();
    }
  }

  searchFilter = () => {
    let query = this.state.query;
    let resultArray = _.cloneDeep(this.props.data);
    let searchArray = resultArray.filter(obj =>
      _.includes(obj.name.toLowerCase(), query.toLowerCase()) ?
        true : false
    )
    this.setState({ result: searchArray });

  }
  handleOnInputChange = (args) => {

    console.log("Argas",args)
    // const query = event.target.value;
    // let searchArray = [];

    // let resultArray = _.cloneDeep(this.props.data);
    // let threshold = this.state.threshold;

    // if (_.isEmpty(query)) {

    //   searchArray = [];
    //   this.setState({ result: searchArray, query, focusedItem: -1 });
    // }
    // else {
    //     searchArray = resultArray.filter(obj =>
    //       _.includes(obj.name.toLowerCase(), query.toLowerCase()) ?
    //         true : false
    //     )
    //     if (((!_.isEqual(query.substr(0, this.props.lastNullQuery.length), this.props.lastNullQuery))&& searchArray.length < threshold)|(_.isEmpty(this.props.lastNullQuery)) ) {
    //     //if(searchArray.length < threshold){  
    //       this.props.getData(query, 1, threshold);
    //     }



    //   this.setState({ result: searchArray, query });
    //}

  }
  debounceFunction = (event) => {

    let td = 1000 ; 
    let fn = this.handleOnInputChange ;

    let debounceTimer;
    console.log("debounce func",event);

    return function () {
      const context = this;
      this.searchQuery = event.target.value;
      this.page = 1 ;
      const args = arguments
      clearTimeout(debounceTimer)
      debounceTimer
        = setTimeout(() => fn.call(context, args), td)
    }





  }

  render() {
    let searchResults = [];
    let result = _.cloneDeep(this.state.result);
    const debouncSearch = debounceFunction();
    //let focusedItem = this.state.focusedItem;


    if (result.length > 0) {
      result.forEach(function (item, index) {
        searchResults.push(
          <div key={item.url} className="padding">
            <span id={"span" + index} key={index} className="eachList">{item.name}</span>
            <span className="gender">{item.gender}</span>
            <div className="bDay">
              {item.birth_year}
            </div>
            <hr className="horizontalLine" />
          </div>
        )
      })
    }

    return (
      <div className="maindiv">
        <h2>
          Search Hacker News
        </h2>
        <div className="dropDown">
          <div className={cx("searchBox")} >
            <img src={searchIcon} alt="searchIcon" className="searchIcon" />
            <input className="search-input"
              id="mySearchInput"
              placeholder="Type here to start searching"
              onChange={this.debounceFunction()}
              //onChange={(event) => this.handleOnInputChange(event, this.props.getData, 2000)}
              autoComplete="off"
            />
          </div>

          {/* <div className="divSearch"  disabled={(focusedItem >=0 && focusedItem < result.length )? false : true}>
          <NavLink to={{
            pathname: "/person/" + focusedItem

          }}
          >

          </NavLink>
          </div> */}

          {this.props.getDataState === "LOADING" ? <div className="loader"></div> :
            this.state.result
              ? <div className="itemResults">
                {searchResults}
              </div>
              : <div className="itemResults"> "NO STATE FOUND" </div>}



        </div>


      </div>
    );
  }

}
