import React, { Component } from 'react';
import { connect } from 'react-redux';
import searchIcon from './search.png';
import './index.css';
import * as actions from './Store/action';
import cx from 'classnames';
import { NavLink } from "react-router-dom";
import _ from 'lodash';
class HomePage extends Component {

  constructor(props) {
    super(props)
    this.state = {
      query: '',
      result: [],
      threshold: 3,
      focusedItem: -1,
    }
    this.debounceTimer = null;
  }

  componentDidMount() {
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.data !== this.props.data) {
      this.searchFilter();
    }
  }

  intersectFunc = (a, b) => {
    var t;
    if (b.length > a.length) {
      t = b;
      b = a;
      a = t; // indexOf to loop over shorter
    }
    return a.filter(function (e) {
      return b.indexOf(e) > -1;
    }).filter(function (e, i, c) { // extra step to remove duplicates
      return c.indexOf(e) === i;
    });

  }
  searchFilter = (query) => {
    let searchArray = [];
    if(_.isEmpty(query))
      query = this.state.query;
    let thisPar = this;
    let resultArray = _.cloneDeep(this.props.data);
    if (_.isEmpty(query)) {
      searchArray = [];
    }
    else {
      console.log("query ",query);

      searchArray = resultArray.filter(function (obj) {

        let keys = ['title', 'url', 'num_comments', 'author', 'story_text']
        var intersectfunc = thisPar.intersectFunc;

        query = query.toLowerCase();
        let queryArray = query.split(" ");
        for (var key in obj) {
          if (obj.hasOwnProperty(key) && _.includes(keys, key)) {
            console.log("");

            return intersectfunc(obj[key].toLowerCase().split(" "), queryArray);

          }
        }

      });

    }

    if (searchArray.length > 10)
      searchArray = searchArray.slice(0, 10);
    this.setState({ result: searchArray });

    return searchArray;

  }

  debounceFunction = (query, page, fn, td) => {

    let thisPar = this;
    return function () {
      const context = this;
      clearTimeout(thisPar.debounceTimer)
      thisPar.debounceTimer = setTimeout(() => fn.call(context, query, page), td);
    }

  }


  handleOnInputChange = (event) => {

    const query = event.target.value;
    let page = 1;
    let searchArray = this.searchFilter(query)
    console.log("searchArray ",searchArray.length);

    if (searchArray.length < 10) {
      const debouncer = this.debounceFunction(query, page, this.props.getData, 2000);
      debouncer();
    }

    this.setState({ query });


  }

  render() {
    let searchResults = [];
    let result = _.cloneDeep(this.state.result);
    if (result.length > 0) {

      result.forEach(function (item, index) {
        searchResults.push(
          <div key={index} className="padding">

            {item.url || item.title ?
              <div className="subList">
                <span className="spanWithBlack" id={"span" + index} key={index} >{item.title ? item.title : ""}</span>
                <span className="storyUrl">{item.url ? '(' + item.url + ')' : ""}</span>

              </div>
              :
              null
            }
            {item.author || item.num_comments ?
              <div className="subList">
                {item.author ?
                  <span>
                    Author: {item.author}
                  </span>
                  :
                  null}
                {item.num_comments ?
                  <span>
                    {item.author ? <React.Fragment> | </React.Fragment> : null}
                    comments: {item.num_comments}
                  </span>
                  :
                  null}
                {item.relevancy_score ?
                  <span>
                    {item.author ? <React.Fragment> | </React.Fragment> : null}
                    relevancy_score: {item.relevancy_score}
                  </span>
                  :
                  null}

              </div>
              : null}
            {item.story_text ? <div className="subList spanWithBlack">
              {item.story_text}
            </div> : null}

            <hr className="horizontalLine" />
          </div>
        )
      })
    }

    return (
      <div className="maindiv">
        <h2>
          Search Hacker News
        </h2>
        <div className="dropDown">
          <div className={cx("")} >
            <img src={searchIcon} alt="searchIcon" className="searchIcon" />
            <input className="searchBox"
              id="mySearchInput"
              placeholder="Type here to start searching"
              onKeyUp={(event) => this.handleOnInputChange(event)}
              autoComplete="off"
            />
          </div>

          {this.props.getDataState === "LOADING" ? <div className="loader"></div> :
            this.state.result
              ? <div className="itemResults">
                {searchResults}
              </div>
              : <div className="itemResults"> "NO DATA FOUND" </div>}



        </div>


      </div>
    );
  }

}
const mapStateToProps = state => {
  return {
    data: state.data,
    getDataState: state.getDataState,
    lastNullQuery: state.lastNullQuery
  };
};



const mapDispatchToProps = dispatch => {
  return {
    getData: (query, page) => dispatch(actions.getData(query, page)),
  };
};

export default (connect(mapStateToProps, mapDispatchToProps)(HomePage));
