import * as actionTypes from './actionTypes';

const initialState = {
    getDataState: 'INIT',
    data:[],
    error:'',
    lastNullQuery:'',
    characterDetails:{},
}

const initState = () => {
    return initialState;
}

//GET DATA REDUCERS
const getDataLoading = (state) => {
    return {...state, 
        getDataState: 'LOADING'
    } ;
}

const getDataSuccess = (state, action) => {
    return {...state,
        getDataState: 'SUCCESS',
        data: action.data,
        lastNullQuery:action.lastNullQuery,
        error: null
    }
};

const getDataError = (state, action) => {
    return {...state,
        getDataState: 'ERROR',
        error: action.error,
    };
};

const setCharacterDetails = (state,action) =>{
    return {...state,
        characterDetails:action.characterDetails
    }
}

const reducer = (state = initialState, action) => {

    switch (action.type) {

        case actionTypes.INIT_STATE: return initState();

        case actionTypes.GET_DATA_LOADING: return getDataLoading(state, action);
        case actionTypes.GET_DATA_SUCCESS: return getDataSuccess(state, action);
        case actionTypes.GET_DATA_ERROR: return getDataError(state, action);

        case actionTypes.SET_CHARACTER_DETAILS: return setCharacterDetails(state,action);
        default: return state;
    }
};

export default reducer;
