import React, { Component } from 'react';
import { connect } from 'react-redux';
import searchIcon from './search.png';
import './index.css';
import * as actions from './Store/action';
import cx from 'classnames';
import { NavLink } from "react-router-dom";
import _ from 'lodash';
class HomePage extends Component {

  constructor(props) {
    super(props)
    this.state = {
      query: '',
      result: [],
      threshold: 3,
      focusedItem: -1,
    }
    this.debounceTimer = null;
  }

  componentDidMount() {
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.data !== this.props.data) {
      this.searchFilter();
    }
  }

  searchFilter = () => {
    let query = this.state.query;
    let resultArray = _.cloneDeep(this.props.data.hits);
    // let searchArray = resultArray.filter(obj =>
    //   _.includes(obj.name.toLowerCase(), query.toLowerCase()) ?
    //     true : false
    // )
    console.log("resultArray", resultArray);

    this.setState({ result: resultArray });

  }

  debounceFunction = (query, page, fn, td) => {

    let thisPar = this;
    return function () {
      const context = this;
      clearTimeout(thisPar.debounceTimer)
      thisPar.debounceTimer = setTimeout(() => fn.call(context, query, page), td);
    }

  }


  handleOnInputChange = (event) => {

    const query = event.target.value;
    let searchArray = [];

    let resultArray = _.cloneDeep(this.props.data);
    let threshold = this.state.threshold;
    let page = 1;
    // if (_.isEmpty(query)) {

    //   searchArray = [];
    //   this.setState({ result: searchArray, query, focusedItem: -1 });
    // }
    // else {
    //     searchArray = resultArray.filter(obj =>
    //       _.includes(obj.name.toLowerCase(), query.toLowerCase()) ?
    //         true : false
    //     )
    //     if (((!_.isEqual(query.substr(0, this.props.lastNullQuery.length), this.props.lastNullQuery))&& searchArray.length < threshold)|(_.isEmpty(this.props.lastNullQuery)) ) {
    //     //if(searchArray.length < threshold){  
    //       this.props.getData(query, 1, threshold);
    //     }



    //   this.setState({ result: searchArray, query });
    // }

    const debouncer = this.debounceFunction(query, page, this.props.getData, 2000);
    debouncer();

  }

  render() {
    let searchResults = [];
    let result = _.cloneDeep(this.state.result);

    if (result.length > 0) {
      result.forEach(function (item, index) {
        searchResults.push(
          <div key={index} className="padding">

            {item.url || item.title ?
              <div className="">
                <span id={"span" + index} key={index} >{item.title ? item.title : ""}</span>
                <span className="storyUrl">{item.url ? item.url : ""}</span>

              </div>
              :
              null
            }
            {item.author || item.num_comments ?
              <div>
                {item.author ?
                  <span>
                    Author: {item.author}
                  </span>
                  :
                  null}
                {item.num_comments ?
                  <span>
                    comments: {item.num_comments}
                  </span>
                  :
                  null}
              </div>
              : null}


            <div className="bDay">
              {item.birth_year}
            </div>
            <hr className="horizontalLine" />
          </div>
        )
      })
    }

    return (
      <div className="maindiv">
        <h2>
          Search Hacker News
        </h2>
        <div className="dropDown">
          <div className={cx("searchBox")} >
            <img src={searchIcon} alt="searchIcon" className="searchIcon" />
            <input className="search-input"
              id="mySearchInput"
              placeholder="Type here to start searching"
              //onKeyUp={this.debounceFunction(this.props.getData,2000)}
              onKeyUp={(event) => this.handleOnInputChange(event)}
              autoComplete="off"
            />
          </div>

          {/* <div className="divSearch"  disabled={(focusedItem >=0 && focusedItem < result.length )? false : true}>
          <NavLink to={{
            pathname: "/person/" + focusedItem

          }}
          >

          </NavLink>
          </div> */}

          {this.props.getDataState === "LOADING" ? <div className="loader"></div> :
            this.state.result
              ? <div className="itemResults">
                {searchResults}
              </div>
              : <div className="itemResults"> "NO DATA FOUND" </div>}



        </div>


      </div>
    );
  }

}
const mapStateToProps = state => {
  return {
    data: state.data,
    getDataState: state.getDataState,
    lastNullQuery: state.lastNullQuery
  };
};



const mapDispatchToProps = dispatch => {
  return {
    getData: (query, page) => dispatch(actions.getData(query, page)),
  };
};

export default (connect(mapStateToProps, mapDispatchToProps)(HomePage));
